const fs = require('fs');
const pathUtil = require('path');
const {
  LANGUAGES,
  LANGUAGES_MD
} = require('./common');

fs.writeFileSync(
  LANGUAGES_MD,
  [
    '<!-- Generated by scripts/generate-languages.md.js -- DO NOT EDIT BY HAND -->',
    '| Language | File |',
    '| -: | :- |',
    ...Array.from(Object.entries(LANGUAGES))
      .map(([code, {name}]) => `| ${name} | ${code}.yaml |`)
  ].join('\n')
);
